import React, { useState } from 'react';
import CourseModal from '../components/courseModal/CourseModal';
import './AllCoursesPage.css';
import 'react-refresh/runtime';

import AWS from '../../assets/coursPictures/AWS.png';
import Azure from '../../assets/coursPictures/Azure.png';
import Business from '../../assets/coursPictures/businessStrategy.png';
import Databricks from '../../assets/coursPictures/Databricks.png';
import ImageRec from '../../assets/coursPictures/ImageRecognition.jpg';
import Java from '../../assets/coursPictures/JavaLogo.png';
import ML from '../../assets/coursPictures/machineLearning.jpg';
import NET from '../../assets/coursPictures/NET.jpg';
import SEO from '../../assets/coursPictures/SEO.png';
import Social from '../../assets/coursPictures/SocialMediaMarketing.jpg';
import SQL from '../../assets/coursPictures/SQL.jpg';

const dummyCourses = [
    { title: 'AWS Fundamentals', image: AWS, description: 'Learn cloud computing with AWS.' },
    { title: 'Azure Cloud Services', image: Azure, description: 'Master Microsoft Azure services.' },
    { title: 'Business Strategy', image: Business, description: 'Craft smart business plans.' },
    { title: 'Databricks Essentials', image: Databricks, description: 'Work with big data using Databricks.' },
    { title: 'Image Recognition with AI', image: ImageRec, description: 'Explore image classification using AI.' },
    { title: 'Java Programming', image: Java, description: 'Learn Java from scratch.' },
    { title: 'Machine Learning Basics', image: ML, description: 'Start your journey into ML.' },
    { title: '.NET for Beginners', image: NET, description: 'Build .NET apps with ease.' },
    { title: 'SEO Optimization', image: SEO, description: 'Improve your site’s search ranking.' },
    { title: 'Social Media Marketing', image: Social, description: 'Grow your brand online.' },
    { title: 'SQL Database Fundamentals', image: SQL, description: 'Master querying with SQL.' },
];

const AllCoursesPage = () => {
    const [searchQuery, setSearchQuery] = useState('');
    const [selectedCourse, setSelectedCourse] = useState(null);

    const filteredCourses = dummyCourses.filter(course =>
        course.title.toLowerCase().includes(searchQuery.toLowerCase())
    );

    return (
        <div className="courses-page">
            <h1>All Courses</h1>

            <input
                type="text"
                className="course-search"
                placeholder="Search for a course..."
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
            />

            <div className="course-grid">
                {filteredCourses.length > 0 ? (
                    filteredCourses.map((course, index) => (
                        <div
                            key={index}
                            className="course-card"
                            onClick={() => setSelectedCourse(course)}
                        >
                            <img src={course.image} alt={course.title} />
                            <div className="course-title">{course.title}</div>
                        </div>
                    ))
                ) : (
                    <p>No courses found.</p>
                )}
            </div>

            <CourseModal course={selectedCourse} onClose={() => setSelectedCourse(null)} />
        </div>
    );
};

export default AllCoursesPage;
