import React from 'react';
import './CartPage.css';
import seoImage from '../../assets/coursPictures/SEO.png'; // Adjust as needed

const CartPage = () => {
    const cartItems = [
        { name: 'Search Engine Optimization', price: 10000 },
        { name: 'course A', price: 10000 },
        { name: 'course B', price: 10000 },
        { name: 'course C', price: 10000 },
    ];
    const total = cartItems.reduce((sum, item) => sum + item.price, 0);

    return (
        <div className="cartPage">
            <h2 className="cartHeading">Your Cart:</h2>

            <div className="cartCardWrapper">
                {/* Course Card Left */}
                <div className="courseCard">
                    <img src={seoImage} alt="Course" />
                    <h3>Search Engine Optimization</h3>
                    <div className="courseChips">
                        <span>Topics:</span>
                        <span>Price:</span>
                        <span>Schedule:</span>
                        <span>Description:</span>
                    </div>
                </div>

                {/* Price Summary Right */}
                <div className="cartSummary">
                    {cartItems.map((item, idx) => (
                        <p key={idx}>
                            {item.name} : <span>{item.price.toLocaleString()} nok</span>
                        </p>
                    ))}
                    <div className="totalPriceBox">
                        <span>Total price :</span>
                        <strong>{total.toLocaleString()} NOK</strong>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default CartPage;
