import React, { useEffect, useRef } from 'react';

const courseSuggestions = [
    'Web Development',
    'AI & Machine Learning',
    'Cloud & DevOps',
    'Digital Marketing',
    'Business Analytics',
    'Cybersecurity',
    'UI/UX Design',
    'Python Programming',
    'Data Science',
];

const SearchPopup = ({ isOpen, onClose, searchQuery, setSearchQuery, onSelect }) => {
    const popupRef = useRef();

    const filteredSuggestions = courseSuggestions.filter(course =>
        course.toLowerCase().includes(searchQuery.toLowerCase())
    );

    useEffect(() => {
        const handleClickOutside = (e) => {
            if (popupRef.current && !popupRef.current.contains(e.target)) {
                onClose();
            }
        };
        document.addEventListener('mousedown', handleClickOutside);
        return () => document.removeEventListener('mousedown', handleClickOutside);
    }, [onClose]);

    if (!isOpen) return null;

    return (
        <div className="overlay-blur">
            <div className="search-popup" ref={popupRef}>
                <input
                    type="text"
                    placeholder="Search for courses"
                    value={searchQuery}
                    onChange={(e) => setSearchQuery(e.target.value)}
                    onKeyDown={(e) => {
                        if (e.key === 'Enter' && filteredSuggestions.length > 0) {
                            onSelect(filteredSuggestions[0]);
                        }
                    }}
                    autoFocus
                />
                {filteredSuggestions.length > 0 && (
                    <div>
                        <p style={{ fontWeight: 'bold' }}>Suggestions:</p>
                        <ul style={{ listStyle: 'none', padding: 0 }}>
                            {filteredSuggestions.map((suggestion, index) => (
                                <li
                                    key={index}
                                    onClick={() => onSelect(suggestion)}
                                    style={{
                                        padding: '0.5rem',
                                        borderRadius: '6px',
                                        cursor: 'pointer',
                                    }}
                                    onMouseEnter={(e) =>
                                        (e.currentTarget.style.backgroundColor = '#ffe600')
                                    }
                                    onMouseLeave={(e) =>
                                        (e.currentTarget.style.backgroundColor = 'transparent')
                                    }
                                >
                                    {suggestion}
                                </li>
                            ))}
                        </ul>
                    </div>
                )}
            </div>
        </div>
    );
};

export default SearchPopup;
