import React, { useState } from 'react';
import './AllCoursesPage.css';

const dummyCourses = [
    {
        title: 'Web Development Bootcamp',
        image: 'https://via.placeholder.com/300x180?text=Web+Development',
    },
    {
        title: 'Introduction to Python',
        image: 'https://via.placeholder.com/300x180?text=Python',
    },
    {
        title: 'Mastering Data Science',
        image: 'https://via.placeholder.com/300x180?text=Data+Science',
    },
    {
        title: 'AI & Machine Learning',
        image: 'https://via.placeholder.com/300x180?text=AI+%26+ML',
    },
    {
        title: 'Cloud Computing with Azure',
        image: 'https://via.placeholder.com/300x180?text=Azure',
    },
    {
        title: 'Digital Marketing 101',
        image: 'https://via.placeholder.com/300x180?text=Marketing',
    },
];

const AllCoursesPage = () => {
    const [searchQuery, setSearchQuery] = useState('');

    const filteredCourses = dummyCourses.filter(course =>
        course.title.toLowerCase().includes(searchQuery.toLowerCase())
    );

    return (
        <div className="courses-page">
            <h1>All Courses</h1>

            <input
                type="text"
                className="course-search"
                placeholder="Search for a course..."
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
            />

            <div className="course-grid">
                {filteredCourses.length > 0 ? (
                    filteredCourses.map((course, index) => (
                        <div key={index} className="course-card">
                            <img src={course.image} alt={course.title} />
                            <div className="course-title">{course.title}</div>
                        </div>
                    ))
                ) : (
                    <p>No courses found.</p>
                )}
            </div>
        </div>
    );
};

export default AllCoursesPage;
